#include<iostream>#include<string>#include<vector>#include<cstdlib>#include<ctime>using namespace std;vector<char> combination(string s,size_t n){	vector<char> vc;	static int first=1;	if(n>=s.size())	{		for(size_t i=0;i<s.size();++i)			vc.push_back(s[i]);		return vc;	}	if(n==0)		return vc;	if(first)	{		first=0;		srand((unsigned long)time(0));	}	for(size_t i=0;i<n;++i)	{		size_t p=rand()%s.size();		vc.push_back(s[p]);		s=s.substr(0,p)+s.substr(p+1);	}	return vc;}void print_vector(vector<char> vc){	for(vector<char>::iterator iter=vc.begin();iter!=vc.end();++iter)	{		cout<<*iter<<endl;	}}int main(){	string s;	while(cin>>s)	{		size_t n;		while(cin>>n)		{			vector<char> vc=combination(s,n);			print_vector(vc);		}	}}